package com.gleasondev.epiqbooksbackend.controller;import com.gleasondev.epiqbooksbackend.entity.Checkout;import com.gleasondev.epiqbooksbackend.service.CheckoutService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.http.HttpStatus;import org.springframework.http.ResponseEntity;import org.springframework.web.bind.annotation.*;import java.util.List;@RestController@RequestMapping("/api/checkout")public class CheckoutController {    private final CheckoutService checkoutService;    @Autowired    public CheckoutController(CheckoutService checkoutService) {        this.checkoutService = checkoutService;    }    @GetMapping    public ResponseEntity<List<Checkout>> getAllCheckouts() {        return ResponseEntity.ok(checkoutService.getAllCheckouts());    }    @PostMapping    public ResponseEntity<Checkout> createCheckout(@RequestBody Checkout checkout) {        return new ResponseEntity<>(checkoutService.saveCheckout(checkout), HttpStatus.CREATED);    }}