package com.gleasondev.epiqbooksbackend.controller;import com.gleasondev.epiqbooksbackend.entity.History;import com.gleasondev.epiqbooksbackend.service.HistoryService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.http.HttpStatus;import org.springframework.http.ResponseEntity;import org.springframework.web.bind.annotation.*;import java.util.List;@RestController@RequestMapping("/api/history")public class HistoryController {    private final HistoryService historyService;    @Autowired    public HistoryController(HistoryService historyService) {        this.historyService = historyService;    }    @GetMapping    public ResponseEntity<List<History>> getAllHistories() {        return ResponseEntity.ok(historyService.getAllHistories());    }    @PostMapping    public ResponseEntity<History> createHistory(@RequestBody History history) {        return new ResponseEntity<>(historyService.saveHistory(history), HttpStatus.CREATED);    }}