package com.gleasondev.epiqbooksbackend.controller;import com.gleasondev.epiqbooksbackend.entity.User;import com.gleasondev.epiqbooksbackend.service.UserService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.http.HttpStatus;import org.springframework.http.ResponseEntity;import org.springframework.web.bind.annotation.PostMapping;import org.springframework.web.bind.annotation.RequestBody;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;import javax.validation.Valid;@RestController@RequestMapping("/api")public class UserController {    @Autowired    private UserService userService;    @PostMapping("/users/register")    public ResponseEntity<User> registerUser(@Valid @RequestBody User user) {        User newUser = userService.saveUser(user);        return new ResponseEntity<>(newUser, HttpStatus.CREATED);    }}