package com.gleasondev.epiqbooksbackend.controller;import com.gleasondev.epiqbooksbackend.requestmodels.UserDto;import com.gleasondev.epiqbooksbackend.service.UserService;import org.springframework.http.ResponseEntity;import org.springframework.web.bind.annotation.PostMapping;import org.springframework.web.bind.annotation.RequestBody;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;@RestController@RequestMapping("/api/users")public class UserController {    private final UserService userService;    public UserController(UserService userService) {        this.userService = userService;    }    @PostMapping("/signup")    public ResponseEntity<?> signUp(@RequestBody UserDto user) {        if (userService.findByUsername(user.getUsername()).isPresent()) {            return ResponseEntity.badRequest().body("Username already exists");        }        userService.save(user);        return ResponseEntity.ok("User registered successfully");    }}