package com.gleasondev.epiqbooksbackend.dto;import com.gleasondev.epiqbooksbackend.entity.Book;import lombok.Getter;import lombok.Setter;@Getter@Setterpublic class BookDTO {    private Long id;    private String title;    private String author;    private String description;    private Integer copies;    private Integer copiesAvailable;    private String category;    private String img;    public BookDTO() {    }    public BookDTO(Book book) {        this.id = book.getId();        this.title = book.getTitle();        this.author = book.getAuthor();        this.description = book.getDescription();        this.copies = book.getCopies();        this.copiesAvailable = book.getCopiesAvailable();        this.category = book.getCategory();        this.img = book.getImg();    }}