package com.gleasondev.epiqbooksbackend.entity;import lombok.Data;import javax.persistence.*;@Entity@Table(name = "history")@Datapublic class History {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    @Column(name = "id")    private Long Id;    @Column(name = "checkout_date")    private String checkoutDate;    @Column(name = "returned_date")    private String returnedDate;    @Column(name = "title")    private String title;    @Column(name = "author")    private String author;    @Column(name = "description")    private String description;    @Column(name = "img")    private String img;    @ManyToOne    @JoinColumn(name = "book_id")    private Book book;    @ManyToOne(fetch = FetchType.LAZY)    @JoinColumn(name = "user_id")    private User user;    public History() {    }    public History(String checkoutDate, String returnedDate, String title, String author,                   String description, String img, Book book, User user) {        this.checkoutDate = checkoutDate;        this.returnedDate = returnedDate;        this.title = title;        this.author = author;        this.description = description;        this.img = img;        this.book = book;        this.user = user;    }}