package com.gleasondev.epiqbooksbackend.entity;import lombok.AllArgsConstructor;import lombok.Data;import lombok.NoArgsConstructor;import javax.persistence.*;import java.time.LocalDate;import java.util.List;@Entity@Data@NoArgsConstructor@AllArgsConstructor@Table(name = "users")public class User {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    private Long id;    @Column(nullable = false, unique = true)    private String email;    @Column(nullable = false)    private String password;    @Column(nullable = false)    private String role;    @Column(nullable = false)    private String firstName;    @Column(nullable = false)    private String lastName;    @Column(nullable = false, unique = true)    private String username;    @Column(nullable = false)    private LocalDate createdDate;    @OneToMany(mappedBy = "user", cascade = CascadeType.ALL, fetch = FetchType.LAZY)    private List<Checkout> checkouts;}