package com.gleasondev.epiqbooksbackend.repository;import com.gleasondev.epiqbooksbackend.entity.Message;import org.springframework.data.domain.Page;import org.springframework.data.domain.Pageable;import org.springframework.data.jpa.repository.JpaRepository;import org.springframework.web.bind.annotation.RequestParam;public interface MessageRepository extends JpaRepository<Message, Long> {    Page<Message> findByUserEmail(@RequestParam("email") String userEmail, Pageable pageable);}