package com.gleasondev.epiqbooksbackend.service;import com.gleasondev.epiqbooksbackend.entity.Book;import com.gleasondev.epiqbooksbackend.repository.BookRepository;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.List;@Servicepublic class BookService {    private final BookRepository bookRepository;    @Autowired    public BookService(BookRepository bookRepository) {        this.bookRepository = bookRepository;    }    public List<Book> getAllBooks() {        return bookRepository.findAll();    }    public Book getBookById(Long id) {        return bookRepository.findById(id)                             .orElseThrow(() -> new RuntimeException("Book not found with id: " + id));    }    public Book saveBook(Book book) {        return bookRepository.save(book);    }    public Book updateBook(Long id, Book updatedBook) {        return bookRepository.findById(id)                             .map(book -> {                                 book.setTitle(updatedBook.getTitle());                                 book.setAuthor(updatedBook.getAuthor());                                 book.setDescription(updatedBook.getDescription());                                                                 return bookRepository.save(book);                             })                             .orElseThrow(() -> new RuntimeException("Book not found with id: " + id));    }    public void deleteBook(Long id) {        bookRepository.deleteById(id);    }}