package com.gleasondev.epiqbooksbackend.service;import com.gleasondev.epiqbooksbackend.entity.Checkout;import com.gleasondev.epiqbooksbackend.repository.CheckoutRepository;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.List;@Servicepublic class CheckoutService {    private final CheckoutRepository checkoutRepository;    @Autowired    public CheckoutService(CheckoutRepository checkoutRepository) {        this.checkoutRepository = checkoutRepository;    }    public List<Checkout> getAllCheckouts() {        return checkoutRepository.findAll();    }    public Checkout saveCheckout(Checkout checkout) {        return checkoutRepository.save(checkout);    }}