package com.gleasondev.epiqbooksbackend.service;import com.gleasondev.epiqbooksbackend.entity.History;import com.gleasondev.epiqbooksbackend.repository.HistoryRepository;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.List;@Servicepublic class HistoryService {    private final HistoryRepository historyRepository;    @Autowired    public HistoryService(HistoryRepository historyRepository) {        this.historyRepository = historyRepository;    }    public List<History> getAllHistories() {        return historyRepository.findAll();    }    public History saveHistory(History history) {        return historyRepository.save(history);    }}